mapper.select:
select id, member_id, password, name, phone, email, create_dt, update_dt
from member
where member_id = #{memberId}

mapper.update:
update member
set password = #{password}, phone = #{phone}, email = #{email}, update_dt = now()
where member_id = #{memberId}

update member
set delete_yn = 'Y', update_dt = now()
where member_id = #{memberId}
